import styled from "styled-components";

export const Container = styled.div`
  svg {
    width: 40px;
    height: 40px;
  }
  svg path {
    fill: ${props =>
      props.currentTheme === "light"
        ? props.theme.backgroundColor
        : props.theme.subHeaderColor};
  }
`;
