import styled, { keyframes } from "styled-components";

export const Container = styled.div`
  cursor: pointer;

  svg {
    width: 30px;
    height: 30px;
    transition: all;

    &:active {
      transform: skew("20deg");
      opacity: 0.5;
    }
  }
  svg path {
    fill: ${props => (props.currentTheme === "light" ? "#0b0316" : "#d6d9e3")};
  }
`;
