import styled, { keyframes } from "styled-components";

const switchAnim = keyframes`
    from{
        transform:skew("20deg")
        opacity:0.5;
    }
    to{
        transform:skew("00deg")
        opacity:1;
    }
`;

export const Container = styled.div`
  cursor: pointer;

  svg {
    width: 30px;
    height: 30px;

    &:active {
      transition: all;
    }
  }
  svg path {
    fill: ${props => (props.currentTheme === "light" ? "#0b0316" : "#d6d9e3")};
  }
`;
