import styled from "styled-components";

export const Container = styled.div`
  height: 100vh;
  width: 100vw;
  background: #e8e9ed;
  margin: 0px;
  display: flex;

  .left-content {
    width: 50vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 5vw;
  }

  .right-content {
    max-width: 50vw;

    .hero-image {
      width: 50vw;
    }
  }

  h1 {
    font-family: "Archivo Black";
    margin: 0px;
    font-weight: 400;
    font-size: 3.5em;
    text-transform: uppercase;
  }

  h4 {
    font-family: "Montserrat";
    font-weight: 300;
    font-size: 1.3em;
    margin: 0px;
    margin-top: 12px;
    margin-bottom: 40px;
    line-height: 30px;
  }

  input {
    height: 50px;
    padding: 12px;
    border: none;
    background: white;
    box-shadow: 8px 4px 12px lightgrey;
  }
`;
