import React, { useContext } from "react";
import { Container } from "./styles";
import { ThemeContext } from "../../context/Theme";
import ThemeSwitcher from "../../component/ThemeSwitcher";

const HomePage = () => {
  const { switchTheme } = useContext(ThemeContext);
  return (
    <Container>
      ThemeSwitcher
      <div className="content">
        <div className="left-content">
          <h1 className="logo archivo">Dink Url shortner</h1>
          <h4 className="sub-text">
            Get your url in seconds. Copy and paste the url here and we will
            give you a short and sweet one. All for 0$.
          </h4>
          <input placeholder="Paste your link here..." />
        </div>
        <div className="right-content">
          <img
            className="hero-image"
            src={require("../../images/hero-image.png")}
            alt="hero-image"
          />
        </div>
      </div>
    </Container>
  );
};

export default HomePage;
