import styled from "styled-components";

export const Container = styled.div`
  height: 100vh;
  width: 100vw;
  background: #e8e9ed;
  margin: 0px;
  display: flex;

  .left-content {
    width: 50vw;
  }

  .right-content {
    max-width: 50vw;

    .hero-image {
      width: 50vw;
    }
  }

  h1 {
    margin: 0px;
  }
`;
