import styled, { keyframes } from "styled-components";

const slideUp = keyframes`
    from{
        transform:translateY(600px);
    }
    to{
        transform:translateY(0px)
    }
`;

export const Container = styled.div`
  height: 100vh;
  width: 100vw;
  background: #e8e9ed;
  margin: 0px;
  display: flex;

  .left-content {
    width: 50vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 5vw;
  }

  .right-content {
    max-width: 50vw;
    display: flex;

    .hero-image {
      width: 45vw;
      animation: ${slideUp} 1s ease-in-out;
    }
  }

  h1 {
    font-family: "Archivo Black";
    margin: 0px;
    font-weight: 400;
    font-size: 3.5em;
    text-transform: uppercase;
  }

  h4 {
    font-family: "Montserrat";
    font-weight: 300;
    font-size: 1.3em;
    margin: 0px;
    margin-top: 12px;
    margin-bottom: 40px;
    line-height: 30px;
  }

  input {
    height: 50px;
    padding: 12px;
    border: none;
    background: white;
    box-shadow: 12px 8px 12px lightgrey;
    font-size: 1.3em;
    font-family: "Montserrat";
  }
`;
