import { useEffect } from "react";
import { useRouter } from "next/router";
import { withApollo } from "react-apollo";
import { gql } from "apollo-boost";

const GET_ORIGINAL_URL = gql`
  query GetOriginal($short: String!) {
    getOriginal(short: $short) {
      original
      short
      id
    }
  }
`;

const Redirect = ({ client }) => {
  const routes = useRouter();

  const getOriginalUrl = useCallback(async () => {
    try {
      const { data } = await client.query({
        query: GET_ORIGINAL_URL,
        variables: { short: router.query.url }
      });
      console.log(data);
    } catch (err) {
      console.log(err.message);
    }
  }, []);

  useEffect(() => {
    getOriginalUrl();
  }, []);

  return null;
};

export default withApollo(Redirect);
